import React, { Component } from 'react';
import axios from 'axios';
// import { API_URL } from "../../../config/";
// import Swal from "sweetalert2";

export class ContactInfo extends Component {
  state = {
    name: "",
    email: "",
    subject: "",
    message: "",
    loading: false,
    sent : false
  };


  handleSubmitForm = (e) => {
    this.setState({ loading : true})
    console.log("Handling form...");
    e.preventDefault();
    let data = {
      name : this.state.name,
      email : this.state.email,
      subject : this.state.subject,
      message: this.state.message
    }
    axios.post('/api/forma', data)
    .then(res => {
      this.setState({
        sent : true,
      }, this.resetForm())
    }).catch(() => {
      console.log("message not sent")
    })
  }

  // For resetting initial data
  resetForm = () => {
    this.setState({ name : "" })
    this.setState({ email : "" })
    this.setState({ subject : "" })
    this.setState({ message : "" })

    setTimeout( () => {
      this.setState({ 
        loading : false
      })
    }, 3000);
  }


  render() {
    const { name, email, subject, message, loading } = this.state;

    return (
      <div id="contact_info_container">
        <div id="contact_information">
          <form onSubmit={this.handleSubmitForm}>
            <label> Your Name (required) </label>
            <input
              id="contact_name"
              type="text"
              name="fname"
              required
              value={name}
              onChange={(e) => this.setState({ name: e.target.value })}
            />
            <label> Your Email (required) </label>
            <input
              id="contact_email"
              type="email"
              required
              value={email}
              onChange={(e) => this.setState({ email: e.target.value })}
            />
            <label> Subject </label>
            <input
              id="contact_subject"
              type="text"
              value={subject}
              onChange={(e) => this.setState({ subject: e.target.value })}
            />
            <label> Your Message </label>
            <textarea
              id="contact_message"
              cols="10"
              rows="10"
              type="text"
              value={message}
              onChange={(e) => this.setState({ message: e.target.value })}
            />
            <br />
            <br />
            <div className="msg"> Message has successfully been sent! </div>
            <input disabled={loading ? true : false} id={loading ? "contact_submit_button_loading": "contact_submit_button"} type="submit" value={loading ? "SENDING" : "SEND"} />
          </form>
        </div>
      </div>
    );
  }
}

export default ContactInfo;
