import { BoundFunctions, Queries } from '@testing-library/dom/get-queries-for-element';
import * as queries from '@testing-library/dom/queries';
import { OptionsReceived } from '@testing-library/dom/node_modules/pretty-format';

export type Screen<Q extends Queries = typeof queries> = BoundFunctions<Q> & {
    /**
     * Convenience function for `pretty-dom` which also allows an array
     * of elements
     */
    debug: (
        element: Element | HTMLDocument | Array<Element | HTMLDocument>,
        maxLength?: number,
        options?: OptionsReceived,
    ) => void;
};

export const screen: Screen;
